import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const msgToSend = input;
    setChat(prev => [...prev, { sender: "user", text: input }]);
    setInput("");

    try {
      // ‚úÖ FIXED URL to call correct backend route
      const res = await axios.post("http://localhost:5000/api/chat", {
        message: msgToSend,
      });

      // ‚úÖ FIXED FIELD (backend returns {reply: "..."} not .data.reply)
      setChat(prev => [...prev, { sender: "ai", text: res.data.reply }]);

    } catch (err) {
      console.error(err);
      setChat(prev => [...prev, { sender: "ai", text: "‚ùå Backend not responding" }]);
    }
  };

  return (
    <div className="app" style={{ padding: 20, maxWidth: 600, margin: "auto" }}>
      <h1>üß† AI Chat Agent</h1>

      <div className="chat-box" style={{ border:"1px solid #ccc",height:400,overflow:"auto",padding:12,borderRadius:10 }}>
        {chat.map((msg, i) => (
          <div key={i} style={{ marginBottom:6,color: msg.sender==="ai"?"blue":"black" }}>
            <b>{msg.sender === "ai" ? "AI" : "You"}:</b> {msg.text}
          </div>
        ))}
      </div>

      <div className="input-area" style={{ display:"flex",gap:8,marginTop:12 }}>
        <input
          style={{ flex:1,padding:10,borderRadius:8,border:"1px solid #aaa" }}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button style={{ padding:"10px 16px",borderRadius:8,background:"#000",color:"#fff" }} onClick={sendMessage}>
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
