// frontend/src/App.js
import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim() || loading) return;

    const msgToSend = input;

    // add user message to chat
    const userMessage = { sender: "user", text: msgToSend };
    setChat((prev) => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      // call your Node backend on port 5000
      const res = await axios.post("http://localhost:5000/api/chat", {
        message: msgToSend,
      });

      const aiText =
        (res.data && res.data.reply) || "‚ö†Ô∏è No reply field in response";

      const aiMessage = { sender: "ai", text: aiText };
      setChat((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error("Frontend error:", err);

      const aiMessage = {
        sender: "ai",
        text:
          "‚ùå Backend not responding. Check if backend is running on http://localhost:5000 and /api/chat route exists.",
      };
      setChat((prev) => [...prev, aiMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  return (
    <div className="app">
      <h1>üß† AI Chat Agent</h1>

      <div className="chat-box">
        {chat.map((msg, i) => (
          <div
            key={i}
            className={msg.sender === "user" ? "user-msg" : "ai-msg"}
          >
            {msg.text}
          </div>
        ))}
        {loading && (
          <div className="ai-msg">
            ‚è≥ Thinking...
          </div>
        )}
      </div>

      <div className="input-area">
        <input
          type="text"
          value={input}
          placeholder="Type your message..."
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button onClick={sendMessage} disabled={loading}>
          {loading ? "Sending..." : "Send"}
        </button>
      </div>
    </div>
  );
}

export default App;
