// frontend/src/App.js
import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const msgToSend = input;

    // Add user message to chat
    const userMessage = { sender: "user", text: msgToSend };
    setChat((prev) => [...prev, userMessage]);
    setInput("");

    try {
      // Call backend on port 5000
      const res = await axios.post("http://localhost:5000/api/chat", {
        message: msgToSend,
      });

      // Backend returns: { reply: "..." }
      const aiMessage = { sender: "ai", text: res.data.reply };
      setChat((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error("Frontend error:", err.message);

      const aiMessage = {
        sender: "ai",
        text: "âŒ Could not reach backend. Check if Node server is running on port 5000.",
      };
      setChat((prev) => [...prev, aiMessage]);
    }
  };

  return (
    <div className="app">
      <h1>ðŸ§  AI Chat Agent (Dummy)</h1>

      <div className="chat-box">
        {chat.map((msg, i) => (
          <div
            key={i}
            className={msg.sender === "user" ? "user-msg" : "ai-msg"}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <div className="input-area">
        <input
          type="text"
          value={input}
          placeholder="Type your message..."
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
