// frontend/src/App.js
import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);
  const [health, setHealth] = useState(null);

  const testBackend = async () => {
    try {
      const res = await axios.get("http://localhost:5000/health");
      setHealth(JSON.stringify(res.data));
    } catch (err) {
      console.error("Health check error:", err);
      setHealth("‚ùå Cannot reach backend /health");
    }
  };

  const sendMessage = async () => {
    if (!input.trim() || loading) return;

    const msgToSend = input;
    const userMessage = { sender: "user", text: msgToSend };

    setChat((prev) => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:5000/api/chat", {
        message: msgToSend,
      });

      const aiText =
        (res.data && res.data.reply) || "‚ö†Ô∏è No reply field from backend";

      const aiMessage = { sender: "ai", text: aiText };
      setChat((prev) => [...prev, aiMessage]);
    } catch (err) {
      console.error("Frontend /api/chat error:", err);
      const aiMessage = {
        sender: "ai",
        text:
          "‚ùå Could not reach backend /api/chat. Check console & backend logs.",
      };
      setChat((prev) => [...prev, aiMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  return (
    <div className="app">
      <h1>üß† AI Chat Agent</h1>

      <button onClick={testBackend} style={{ marginBottom: "10px" }}>
        üîç Test Backend Connection
      </button>
      {health && (
        <div style={{ marginBottom: "10px", fontSize: "14px" }}>
          Backend health: {health}
        </div>
      )}

      <div className="chat-box">
        {chat.map((msg, i) => (
          <div
            key={i}
            className={msg.sender === "user" ? "user-msg" : "ai-msg"}
          >
            {msg.text}
          </div>
        ))}
        {loading && <div className="ai-msg">‚è≥ Thinking...</div>}
      </div>

      <div className="input-area">
        <input
          type="text"
          value={input}
          placeholder="Type your message..."
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button onClick={sendMessage} disabled={loading}>
          {loading ? "Sending..." : "Send"}
        </button>
      </div>
    </div>
  );
}

export default App;
